import React, { useState } from 'react';
import { vehicleData } from './data/vehicleData';
import Header from './components/Header';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [reservations, setReservations] = useState([]);

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="App">
      <Header 
        currentView={currentView} 
        onViewChange={handleViewChange}
        reservationCount={reservations.length}
      />

      <main style={{padding: '2rem', textAlign: 'center'}}>
        <h2>車両一覧</h2>
        <p>利用可能な車両: {vehicleData.length}台</p>
        <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem', marginTop: '2rem'}}>
          {vehicleData.map(vehicle => (
            <div key={vehicle.id} style={{border: '1px solid #ddd', borderRadius: '8px', padding: '1rem', textAlign: 'left'}}>
              <h3>{vehicle.name}</h3>
              <p>種類: {vehicle.type === 'car' ? '車' : 'バイク'}</p>
              <p>カテゴリ: {vehicle.category}</p>
              <p>料金: ¥{vehicle.price.toLocaleString()}/日</p>
              <p>{vehicle.description}</p>
            </div>
          ))}
        </div>
      </main>

      <footer style={{background: '#333', color: 'white', padding: '1rem', textAlign: 'center'}}>
        <p>&copy; 2024 RentalEasy. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
